import React, { Component } from 'react';
import { TEA_ROUTE, COUNTRY_ROUTE } from './apiConstants';

//przesłać id w propsach albo routerem? i wrzucić do tea-route
class Tea extends Component {

    state = {
        name: '',
        country: '',
        info: '',
        brewing: '',
        smell: '',
        taste: '',
        aroma: '',
        comment: '',
        // date: '',
    }



    //GET bez body!

    componentDidMount(){
        fetch(`${TEA_ROUTE}/1`)
            .then(response => response.json())
            .then(data => {
                console.log('teaData: ', data);
                this.setState({
                    name: data.name,
                    country: data.country,
                    info: data.info,
                    brewing: data.brewing,
                    smell: data.smell,
                    taste: data.taste,
                    aroma: data.aroma,
                    comment: data.comment,
                })
            })

            .catch(error => {
                console.log('teaError', error);
            });
        }

    render(){

        const {name, country, info, brewing, smell, taste, aroma, comment} = this.state;

        return(
            <div>
               {name}
            </div>
        )
    }
}

export default Tea;
