import React, {useState} from 'react';
import { TEA_ROUTE } from './apiConstants';


const newTea = {
        name: '',
        comment: '',
        };

const TeaForm = (props) => {

    const [state, setState] = useState(newTea);

    const handleChange = key => (e) => {
        setState(prevState => {
            return {
                ...prevState,
                [key]: e.target.value,
            }
        })

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('state:', state);

    }    

        fetch(TEA_ROUTE, {
            method: "POST",
            body: JSON.stringify(newTea),
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(response => response.json())
            .then(data => {
                console.log('data: ', data);
            })
            .catch(error => {
                console.log('error', error);
            });
    }

    return (    
            <form onSubmit={handleSubmit}>
                <label>Nazwa:</label>
                <input type='text' name='name' value='' onChange={handleChange('name')}></input>
                <label>Uwagi:</label>
                <textarea type='text' name='comment' value='' onChange={handleChange('comment')}/>
                <button type='submit'>Zapisz</button>
            </form> 
    )
}

export default TeaForm;

