import React, {useState} from 'react';
import { TEA_ROUTE } from './apiConstants';


const newTea = {
        name: '',
        comment: '',
        };

const TeaForm = (props) => {

    const [state, setState] = useState(newTea);
    function stupidFunctin(key) {
        return function handleEvent(event) {
            return key + event
        }
    }
    const func = a =>     a

    const handler = stupidFunctin('your maw! ')    
    console.log(handler(':)'))
    const handleSubmit = key => (e) => {
        setState(prevState => {
            return {
                ...prevState,
                [key]: e.target.value,
            }
        })

        fetch(TEA_ROUTE, {
            method: "POST",
            body: JSON.stringify(newTea),
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(response => response.json())
            .then(data => {
                console.log('data: ', data);
            })
            .catch(error => {
                console.log('error', error);
            });
    }

    return (

            <form onSubmit={handleSubmit}>
                <label>Nazwa:</label>
                <input type='text' name='name' value='' onChange={handleSubmit('name')}></input>
                <label>Uwagi:</label>
                <textarea type='text' name='comment' value='' onChange={e => setState(e.target.event)}/>
                <button type='submit'>Zapisz</button>
            </form> 
    )
}

export default TeaForm;

