        e.preventDefault();

        console.log('EditTeaForm-state:', this.state);
        // validation: nazwa

        const data = {...this.state};
        const id = this.props.match.params.id;
        fetch(`${TEA_ROUTE}/${id}`, {
            method: "PATCH",
            body: JSON.stringify(data),
            headers: {
                "Content-Type": "application/json"
            }
        })
            .then(response => response.json())
