    state = {
        name: '',
        country: '',
        info: '',
        brewing: '',
        smell: '',
        taste: '',
        aroma: '',
        comment: '',
        // date: '',
    }



    //GET bez body!

    componentDidMount(){
        fetch(`${TEA_ROUTE}/1`, {
            method: "GET",
            body: JSON.stringify(),
            headers: {
                "Content-Type": "application/json"
            }
            })
            .then(response => response.json())
            .then(data => {
                console.log('teaData: ', data);
                this.setState({
                    name: data.name,
                    country: data.country,
                    info: data.info,
                    brewing: data.brewing,
                    smell: data.smell,
                    taste: data.taste,
                    aroma: data.aroma,
                    comment: data.comment,
                })
            })

            .catch(error => {
                console.log('teaError', error);
            });
        }

    render(){

        const {name, country, info, brewing, smell, taste, aroma, comment} = this.state;

        return(
            <div>
                <table>
                    <thead>
                        <tr> {name}</tr>
                    </thead>
                    <tbody>
                        <tr>
                            Kraj pochodzenia: {country}
                        </tr>
                        <tr>

                        </tr>


                    </tbody>
                </table>
            </div>
        )
    }
}

export default Tea;
