import React, { Component } from 'react';
import { TEA_ROUTE } from './apiConstants';
import { Link } from 'react-router-dom';

//przesłać id w propsach albo routerem? i wrzucić do tea-route
class Tea extends Component {

    state = {
        name: '',
        country: '',
        info: '',
        brewing: '',
        smell: '',
        taste: '',
        aroma: '',
        comment: '',
        // date: '',
    }

    componentDidMount(){
        fetch(`${TEA_ROUTE}/${this.props.match.params.id}`)
            .then(response => response.json())
            .then(data => {
                console.log('Tea-data: ', data);
                this.setState({
                    name: data.name,
                    country: data.country,
                    info: data.info,
                    brewing: data.brewing,
                    smell: data.smell,
                    taste: data.taste,
                    aroma: data.aroma,
                    comment: data.comment,
                })
            })
            .catch(error => {
                console.log('Tea-error', error);
            });
        }

    render(){

        const {name, country, info, brewing, smell, taste, aroma, comment} = this.state;

        return(
            <div>
                <div>
                {name}
                </div>
                <div>
                    Kraj pochodzenia:{country}
                    Dodatkowe informacje: {info}
                </div>
                <div>
                    Sposób parzenia: {brewing}
                </div>
                <div>
                    Wrażenia zapachowe: {smell}
                    Wrażenia smakowe: {taste}
                    Aromat: {aroma}
                </div>
                <div>
                    {comment}
                </div>
                <div>FOTO</div>
                <Link to='/teaList'>
                    <button>Wróć do listy</button>
                </Link>
            </div>    
        )
    }
}

export default Tea;
