import React, {useState, useEffect} from 'react';
import { TEA_ROUTE } from './apiConstants';
import TeaString from './TeaString';


const TeaList = () => {

    const [teaList, setTeaList] = useState([]);
    const [sortingValue, setSortingValue] = useState('date');

    useEffect(() => {
        fetch(TEA_ROUTE)
            .then(response => response.json())
            .then(data => {
                console.log('TeaList-data:', data);
                setTeaList(data);
            })
            .catch(error => {
                console.log('TeaList-error:', error);
            });
    }, []);

    const handleSort = (e) => {
        setSortingValue({
            [e.target.name]: e.target.value,
        });
        fetch(TEA_ROUTE)
        .then(response => response.json())
        .then(data => {
            console.log('TeaList-data:', data);
            setTeaList(data);
        })
        .catch(error => {
            console.log('TeaList-error:', error);
        });
    }

    // const tealist1 = teaList[0]

    // console.log('nr1:', tealist1.name)
    // const sortByDate = (a, b) => {
    //     console.log('sortTeaListDate:', teaList)
    //     teaList.sort((a.date, b.date) => {b.date - a.date})
    //     console.log('sortedTeaListDate', teaList)
    // }


    function compare( a, b ) {

        if ( a.date < b.date ){
          return -1;
        }
        if ( a.date > b.date ){
          return 1;
        }
        return 0;
      }


    return(
        <div className='container'>
            <select
                name='sprt'
                value={sortingValue}
                onChange={handleSort}>
                    <option>Wybierz</option>
                    <option value="date">dacie</option>
                    <option value="name">nazwie</option>
                    <option value="type">typie</option>
                    <option value="country">kraju</option>
            </select>
            <ul>
                {(teaList.sort(compare)).map(tea => {
                    return (
                        <li key={tea.id}> 
                            <TeaString name={tea.name} country={tea.country} date={tea.date} id={tea.id} /> 
                        </li>
                    )
                })}
            </ul>
        </div>
    )
}

export default TeaList;
